#encoding:utf-8require 'selenium-webdriver'describe 'forward and back test' do	before(:all) do		@dr = Selenium::WebDriver.for :chrome		@url_163 = 'http://www.163.com/'		@url_news = 'http://news.baidu.com/'	end		it 'from news back 163' do		@dr.get @url_163		sleep 2		@dr.get @url_news		sleep 2		@dr.navigate.back()		sleep 1		@dr.current_url.should == @url_163	end		it 'from 163 forward to news' do		@dr.get @url_news		@dr.get @url_163		@dr.navigate.back()		@dr.navigate.forward()		@dr.current_url.should == @url_163	end	after(:all) do		sleep 2		@dr.quit()	endend